mapper.select:
SELECT *
FROM MEMBER
WHERE MEMBER_ID = #{memberId} AND MEMBER_PWD = #{memberPwd}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_NICKNAME = #{memberNickname}

SELECT COUNT(*) 
FROM MEMBER 
WHERE MEMBER_ID LIKE #{memberId}

mapper.update:
UPDATE REVIEW
SET STATUS = 'N'
WHERE REF_MEMBER_NO LIKE #{memberNo}
AND REVIEW_NO LIKE #{reviewNo}

UPDATE MEMBER
SET 

	MEMBER_NAME = #{memberName}


	
		,MEMBER_NICKNAME = #{memberNickname}
	
	
		,BIRTHDAY = #{birthday}
	
	
		,ZIPCODE = #{zipcode}
		,ADDRESS_1 = #{address1}
		,ADDRESS_2 = #{address2}
		,ADDRESS_3 = #{address3}
	
	
		,PHONE = #{phone}
	

WHERE MEMBER_NO LIKE #{memberNo}

UPDATE MEMBER
SET MEMBER_PWD = #{memberPwd}
WHERE MEMBER_NO LIKE #{memberNo}
