mapper.select:
SELECT COUNT(*) FROM PRODUCT P JOIN MODEL M ON(M.MODEL_NO = P.REF_MODEL_NO) JOIN BRAND B ON(B.BRAND_NO = M.REF_BRAND_NO) WHERE P.STATUS LIKE 'Y'
AND P.REF_CATEGORY_NO LIKE #{categoryNo}
AND P.REF_TYPE_NO LIKE #{typeNo}
AND B.BRAND_NO LIKE #{brandNo}
AND M.MODEL_NO IN
#{modelNo}
AND PRODUCT_PRICE BETWEEN (SELECT PRICE_MIN FROM PRICE WHERE PRICE_NO LIKE #{priceNo}) AND (SELECT PRICE_MAX FROM PRICE WHERE PRICE_NO LIKE #{priceNo})

SELECT P.PRODUCT_NO PRODUCT_NO, B.BRAND_NAME BRAND_NAME, P.PRODUCT_NAME PRODUCT_NAME, P.PRODUCT_PRICE PRODUCT_PRICE, NVL(AVG(R.RATING),0) RATING, COUNT(R.REVIEW_NO) COUNT FROM PRODUCT P JOIN MODEL M ON(M.MODEL_NO = P.REF_MODEL_NO) JOIN BRAND B ON(B.BRAND_NO = M.REF_BRAND_NO) LEFT JOIN REVIEW R ON(R.REF_PRODUCT_NO = P.PRODUCT_NO) WHERE P.STATUS LIKE 'Y' AND (R.REVIEW_NO IS NULL OR (R.REVIEW_NO IS NOT NULL AND R.STATUS LIKE 'Y'))
AND P.REF_CATEGORY_NO LIKE #{categoryNo}
AND P.REF_TYPE_NO LIKE #{typeNo}
AND B.BRAND_NO LIKE #{brandNo}
AND M.MODEL_NO IN
#{modelNo}
AND (PRODUCT_PRICE BETWEEN (SELECT PRICE_MIN FROM PRICE WHERE PRICE_NO LIKE #{priceNo}) AND (SELECT PRICE_MAX FROM PRICE WHERE PRICE_NO LIKE #{priceNo}))
GROUP BY PRODUCT_NO, BRAND_NAME, PRODUCT_NAME, PRODUCT_PRICE ORDER BY P.PRODUCT_NO DESC

SELECT REF_PRODUCT_NO , PRODUCT_ATTACHMENT_ORIGIN_NAME , PRODUCT_ATTACHMENT_CHANGE_NAME , PRODUCT_ATTACHMENT_FILE_PATH FROM PRODUCT_ATTACHMENT WHERE PRODUCT_ATTACHMENT_CHANGE_NAME LIKE '%-1%' AND REF_PRODUCT_NO IN
#{productNo}
ORDER BY PRODUCT_ATTACHMENT_NO DESC
