mapper.select:
SELECT COUNT(*)
FROM WISHLIST
WHERE REF_MEMBER_NO LIKE #{memberNo}
AND REF_PRODUCT_NO LIKE #{productNo}

mapper.insert:
INSERT INTO WISHLIST(
	REF_MEMBER_NO, 
	REF_PRODUCT_NO
)
VALUES(
	#{memberNo},
	#{productNo}
)

mapper.delete:
DELETE FROM WISHLIST
WHERE REF_MEMBER_NO LIKE #{memberNo}
AND REF_PRODUCT_NO LIKE #{productNo}

mapper.select:
SELECT P.PRODUCT_NAME , P.PRODUCT_PRICE , C.QUANTITY AS PRODUCT_QUANTITY FROM PRODUCT JOIN CART C ON(P.PRODUCT_NO=C.REF_PRODUCT_NO) WHERE CART_NO IN
#{cartNo}
ORDER BY P.PRODUCT_PRICE DESC

mapper.insert:
INSERT INTO TOTAL_ORDER(
    ORDER_NO ,
	REF_MEMBER_NO ,
	PAYMENT
	)
VALUES(
	SEQ_ORDER.NEXTVAL,
	#{memberNo},
	#{payment}
)

mapper.update:
UPDATE TOTAL_ORDER
SET STATUS = 'Y'
WHERE STATUS LIKE 'N'
AND REF_MEMBER_NO LIKE #{memberNo}
AND ORDER_DATE LIKE (
	SELECT MAX(ORDER_DATE) 
	FROM TOTAL_ORDER 
	WHERE REF_MEMBER_NO LIKE #{memberNo}
)

mapper.delete:
DELETE FORM  TOTAL_ORDER
WHERE STATUS LIKE 'N'
AND REF_MEMBER_NO LIKE #{memberNo}

mapper.select:
INSERT INTO PRODUCT_ORDER(
	REF_ORDER_NO ,
	REF_PRODUCT_NO ,
	QUANTITY
)
VALUES

(
	 #{po.orderNo}
	 ,#{po.productNo}
	 ,#{po.quantity}
